# ref - https://docs.python.org/3/library/unittest.html 
import unittest
from utils import utils as torch

class TestUtilsRev(unittest.TestCase):

    def test_posint(self):
        np = torch()
        self.assertEqual(np.reversed(12345678), 87654321)

    def test_negint(self):
        np = torch()
        self.assertEqual(np.reversed(-12345678), -87654321)

    def test_negflt(self):
        np = torch()
        self.assertEqual(np.reversed(-12345678.2), -1)
    
    def test_posflt(self):
        np = torch()
        self.assertEqual(np.reversed(12345678.2), -1)

    def test_str1(self):
        np = torch()
        self.assertEqual(np.reversed("qalgbhqwu9trghwuiklghquipgyhqtyquiortyq49utgyhqwuilghqlijgkhqjklghadsghasklghdaskfhasjklfhaskljfhdasjklfhjklashdgkljaghaskl"), -1)
    
    def test_str2(self):
        np = torch()
        self.assertEqual(np.reversed(""), -1)

class TestUtilsBased(unittest.TestCase):

    def test_posint(self):
        np = torch()
        self.assertEqual(np.formatter(12345678), ('0b101111000110000101001110', '0o57060516'))

    def test_negint(self):
        np = torch()
        self.assertEqual(np.formatter(-12345678), ('-0b101111000110000101001110', '-0o57060516') )

    def test_negflt(self):
        np = torch()
        self.assertEqual(np.formatter(-12345678.2), (-1,-1))
    
    def test_posflt(self):
        np = torch()
        self.assertEqual(np.formatter(12345678.2), (-1,-1))

    def test_str1(self):
        np = torch()
        self.assertEqual(np.formatter(""), (-1,-1))
    
    def test_stdr2(self):
        np = torch()
        self.assertEqual(np.formatter(""), (-1,-1))

if __name__ == '__main__':
    unittest.main()